				circle : { speed : .2, easing : 'ease-in-out' },
				boxfill : { speed : .8, easing : 'ease-in' },
				swirl : { speed : .8, easing : 'ease-in' },
				diagonal : { speed : .2, easing : 'ease-in-out' },
